# Cell
def crop_zeros(img,crop_mask):
    crop_mask = np.array(crop_mask).astype('int')
    class_img = np.array(img) * np.stack([crop_mask]*3).T
    true_points = np.argwhere(crop_mask)
    top_left = true_points.min(axis=0)
    bottom_right = true_points.max(axis=0)
    return (class_img[top_left[0]:bottom_right[0]+1,
                      top_left[1]:bottom_right[1]+1]).astype(np.uint8)

